import { createRouter, createWebHistory } from 'vue-router'
import indexPage from '@/pages/indexPage.vue'
import blogDetailsPage from '@/pages/blogDetailsPage.vue'
import blogPage from '@/pages/blogPage.vue'
import ourProjectPage from '@/pages/ourProjectPage.vue'
import projectDetailsPage from '@/pages/projectDetailsPage.vue'
import notFoundPage from '@/pages/notFoundPage.vue'

const routes = [
    {
        path: '/',
        name: 'home',
        component: indexPage
    },
    // {
    //     path: '/home-projects', // Хэш в пути
    //     name: 'home-projects',
    //     component: indexPage
    // },
    {
        path: '/blog',
        name: 'Blog',
        component: blogPage
    },
    {
        path: '/project',
        name: 'Project',
        component: ourProjectPage
    },
    {
        path: '/about',
        name: 'About',
        component: blogDetailsPage
    },
    {
        path: '/catalog/:pageNumber?',
        name: 'Catalog',
        component: projectDetailsPage
    },
    {
        path: '/:CatchAll(.*)',
        name: '404',
        component: notFoundPage
    },
]

const router = createRouter({
    history: createWebHistory(),
    routes
})

window.addEventListener('hashchange', () => {
    const hash = window.location.hash.slice(1) // Убираем символ '#'
    // Вы можете выполнить логику для прокрутки страницы к нужной секции или обработки хэша здесь
})

export default router
